function sum(invoices, property) {
    return invoices.reduce(function(acc, invoice) {
        acc[invoice[property]] = acc[invoice[property]] ? acc[invoice[property]] + 1 : 1;
        return acc;
    }, {});
}

function getValues(object) {
    return Object.keys(object).map(function (key) {
        return object[key];
    });
}

function getKeys(object) {
    return Object.keys(object);
}

function compareDate(actual, other) {
    var actualDate = new Date(actual.invoiceDate);
    var otherDate = new Date(other.invoiceDate);

    if (actualDate < otherDate)
        return -1;
    if (actualDate > otherDate)
        return 1;
    return 0;
}

if(angular.isDefined($data.invoices) && $data.invoices){
    var invoicesPerDepartments = sum($data.invoices, 'department');
    var invoicesPerCustomer = sum($data.invoices, 'customerName');

    var invoicesPerDays = sum($data.invoices.sort(compareDate)
        .map(function(invoice) {
            invoice.weekday = new Date(invoice.invoiceDate).getDay();
            return invoice;
        }), 'weekday');


    var invoicesPerMonth = sum($data.invoices.sort(compareDate)
        .map(function(invoice) {
            invoice.month = new Date(invoice.invoiceDate).getMonth();
            return invoice;
        }), 'month');
}

return {
    departments: {
        data: getValues(invoicesPerDepartments),
        labels: getKeys(invoicesPerDepartments)
    },
    days: {
        data: getValues(invoicesPerDays),
        labels: getKeys(invoicesPerDays).map(function(weekday) {
            return $data.weekdays[weekday];
        })
	},
    months: {
        data: getValues(invoicesPerMonth),
        labels: getKeys(invoicesPerMonth).map(function(month) {
            return $data.months[month];
        })
    },
    customers: {
        data: getValues(invoicesPerCustomer),
        labels: getKeys(invoicesPerCustomer)
    }
};

