formControl.value:
var formControl = {
    'readonly' : {
        'chemist': true, 
        'NKC':true, 
        'projectLead': true,
        'PCS': true
    },
    'hidden' : {
        'chemist': true, 
        'NKC':true, 
        'PCS':true,
        'projectLead':true,
        'submitButton' : true,
        'createButton' : true
    }
}

if (!  $data.formInput)
    return formControl;

if (! $data.formInput.drugSubstance) {
    $data.formInput.drugSubstance ={};
}
if ($data.formInput.context.isProcessInstanciation)
{
    console.log("ProcessInstantiation drug="+$data.formInput.drugSubstance);
    formControl.readonly.projectLead=false;
    formControl.hidden.projectLead=false;
    formControl.hidden.createButton=false;
        console.log("ProcessInstantiation projectLeader="+$data.formInput.context.username);
    // project leader is the user name
    $data.formInput.drugSubstance.projectLeader = $data.formInput.context.username;
    $data.formInput.drugSubstance.eln="This is ELN";
    $data.formInput.drugSubstance.sap="This is SAP";
    console.log("ProcessInstantiation end");
}
if ($data.formInput.context.isProcessOverview)
{
    formControl.hidden.projectLead=false;
    formControl.hidden.chemist=false;
    formControl.hidden.NKC=false;
    formControl.hidden.projectLead=false;
    formControl.hidden.PCS=false;

}

if ($data.formInput.formCode =='CHEMIST')
{
    formControl.readonly.chemist=false;
    formControl.hidden.projectLead=false;
    formControl.hidden.chemist=false;
    formControl.hidden.submitButton=false;
}
if ($data.formInput.formCode =='NKC')
{
    formControl.readonly.NKC=false;
    formControl.hidden.projectLead=false;
    formControl.hidden.chemist=false;
    formControl.hidden.NKC=false;
    formControl.hidden.submitButton=false;
}
if ($data.formInput.formCode =='GMP')
{
    formControl.hidden.projectLead=false;
    formControl.hidden.chemist=false;
    formControl.hidden.NKC=false;
    formControl.hidden.submitButton=false;
}
if ($data.formInput.formCode =='PCS')
{
    formControl.hidden.projectLead=false;
    formControl.hidden.chemist=false;
    formControl.hidden.NKC=false;

    formControl.hidden.PCS=false;
    formControl.readonly.PCS=false;
    formControl.hidden.submitButton=false;

    // Calculate the SMILES value
    $data.formInput.drugSubstance.smile="Please calculate the SMILE in the formControl variable"
}
return formControl;
